cat ./outputfiles/preframe.csv | dos2unix |  gawk -F "," '{ lemma[++nr]=$1; for(i=2; i<=NF; i++) {feats[$i]=$i; feat[nr,$i]="T"; } } END { printf "Lemma"; PROCINFO["sorted_in"]="@ind_str_asc"; for(i in feats) printf "\t%s", i; printf "\n"; for(j=1; j<=nr; j++) { printf "%s", lemma[j]; for(i in feats) if(feat[j,i]=="T") printf "\tT"; else printf "\tF"; printf "\n"; } }' > ./outputfiles/dataframe.csv